import { useState } from 'react';
import './App.css';
function App() {

  var time_play = 0.0;
  var is_play = false;
  var auto_play = false;
  const [point, setPoint] = useState(5);
  const [current_point, setCurrentPoint] = useState(0);
  const [pre_point, setPrePoint] = useState(0);
  const handle_point = (e) => {
    const value = e.target.value;
    const check_natural_number = /^[0-9]*$/;
    if (check_natural_number.test(value)) {
      setPoint(value);
    }
    else if (value === '') {
      alert("Please enter a natural number!");
    }
    else {
      alert("Please enter a natural number!");
    }
  }
  const handle_auto_play_on = (current_point) => {
    if (is_play === true) {
      setCurrentPoint(1);
      do {
        if (point < 0) {
          alert("The point must be greater than 0!");

          break;
        }
        if (current_point === point) {
          // all cleared
        }
        if (auto_play === true) {
          current_point++;
          //remove
        }
        else break;
      } while (current_point <= point);
    } else {
      alert("Please press Play button!");
    }

  }
  const handle_play = () => {
    is_play = true;
    const ele_play_area = document.getElementById('play-area');
    if (ele_play_area.getElementsByTagName('div').length > 0) {
      ele_play_area.innerHTML = '';
    }
    //
    var i = point;
    while (i >0) {
      //create point
      const ele_point = document.createElement('div');
      ele_point.className = 'point-ele';
      ele_point.id = 'ele-point';
      ele_point.textContent = i;

      //position
      const rect = ele_play_area.getBoundingClientRect();
      const ele_play_area_width = rect.width;
      const ele_play_area_height = rect.height;
      let x = 0;
      let y = 0;
      do {
         x = Math.random() * ele_play_area_width*0.9 ;
        y = Math.random() * ele_play_area_height * 0.9 ;
      } while ((x < 0 && y > ele_play_area_height)|| (x>ele_play_area_width&&y<0));


      ele_point.style.left = `${x}px`;
      ele_point.style.top = `${y}px`;
      ele_point.style.zIndex = point - i;

      //click
      ele_point.addEventListener('click', (e) => {
        handle_click(e);
      })
      ele_play_area.appendChild(ele_point);
      i--;
    }
  }
  const handle_restart = () => {

  }
  const handle_auto_play_off = () => {

  }
  const handle_click = (e) => {
    const ele_point = document.getElementById('ele-point').te;
    ele_point.style.backgroundColor = 'purple';

    setCurrentPoint(parseInt(ele_point.textContent));
    if (pre_point + 1 === current_point) {

      ele_point.classList.add('fade-out');
      ele_point.classList.add('hidden');
      //remove
      setTimeout(() => {
        ele_point.remove();
        // current_point = parseInt(ele_point.textContent);
        setPrePoint(parseInt(ele_point.textContent));
        if (current_point == point) {
          // all cleared
          const ele_content_game = document.getElementById('content-game');
          ele_content_game.textContent = 'ALL CLEARED';
          ele_content_game.style.color = 'green';
        }
      }, 3000);

    } else {
      //stop

      //game over
      const ele_content_game = document.getElementById('content-game');
      ele_content_game.textContent = 'GAME OVER';
      ele_content_game.style.color = 'red';
    }
  }

  return (

    <div className="App flex">
      <div className='control w-1/3'>
        <h3 id='content-game'>LET'S PLAY</h3>
        <div className='point-area'>
          <h5>Points :  </h5>
          {/* chặn chỉ cho nhập số */}
          <input className='input-point ' maxLength={4} autoComplete='on' value={point} type='text' placeholder='Enter a natural number' onChange={handle_point}></input>
        </div>
        <div className='time-area'>
          <h5>Time :  </h5>
          <p className='time-value'>{time_play}s</p>

        </div>
        <div className='button-area'>
          {is_play === false ?
            <div>
              <button className='button-play' onClick={handle_play}>Play</button>
              <button className='button-auto' onClick={handle_auto_play_on}>Auto play ON</button>
            </div>
            :
            <div>
              <button className='button-play' onClick={handle_play}>Restart</button>
              <button className='button-auto' onClick={handle_auto_play_on}>Auto play OFF</button>
            </div>
          }
        </div>
      </div>
      <div id='play-area' className='play-area '>

      </div>

    </div>
  );
}
export default App;
