import logo from './logo.svg';
import './App.css';
function App() {

  var time_play = 0.0;
  var is_play = false;
  var point = 5;
  var current_point = 1;
  var pre_point = 0;
  const handle_auto_play_on = (current_point) => {
    do {
      if (current_point == point) {
        // all cleared
      }
      if (is_play == true) {
        current_point++;
        //remove
      }
      else break;
    } while (current_point <= point);
  }
  const handle_play = () => {
    const ele_play_area = document.getElementById('play-area');
    if (ele_play_area.getElementsByTagName('div').length > 0) {
      ele_play_area.innerHTML = '';
    }
    //
    var i = 1;
    while (i <= point) {
      const ele_point = document.createElement('div');
      ele_point.className = 'w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center m-2';
      ele_point.textContent = i;
      ele_play_area.appendChild(ele_point);
      i++;
    }
  }
  const handle_restart = () => {

  }
  const handle_auto_play_off = () => {

  }
  const handle_click = (current_point, pre_point) => {
    if (current_point == point) {
      // all cleared
    }
    if (pre_point + 1 == current_point) {
      //remove
    } else {
      //stop

      //game over
    }
  }
  const check_point = (point) => {

  }
  return (

    <div className="App flex">
      <div className='control w-1/3'>
        <h3>LET'S PLAY</h3>
        <div className='point-area'>
          <h5>Points :  </h5>
          {/* chặn chỉ cho nhập số */}
          <input className='input-point border border-black p-4' maxLength={5} autoComplete='on' value={point} type='number' onChange={(e) => point = e.targer.value}></input>
        </div>
        <div className='time-area'>
          <h5>Time :  </h5>
          <p className='time-value'>{time_play}s</p>

        </div>
        <div className='button-area'>
          {is_play === false ?
            <div>
              <button className='button-play' onClick={handle_play}>Play</button>
              <button className='button-auto' onClick={handle_auto_play_on}>Auto play ON</button>
            </div>
            :
            <div>
              <button className='button-auto' onClick={handle_play}>Restart</button>
              <button className='button-auto' onClick={handle_auto_play_on}>Auto play OFF</button>
            </div>
          }
        </div>
      </div>
      <div id='play-area' className='play-area w-2/3 boder boder-black p-4 w-full h-500'>

      </div>

    </div>
  );
}
export default App;
